    --gradient-accent: linear-gradient(135deg, hsl(239 84% 67%), hsl(239 84% 57%));
    --gradient-subtle: linear-gradient(180deg, hsl(0 0% 100%), hsl(220 13% 98%));
    --gradient-success: linear-gradient(135deg, hsl(142 76% 36%), hsl(142 76% 30%));
    --gradient-card: linear-gradient(145deg, hsl(0 0% 100%), hsl(220 13% 99%));

    /* Enhanced Shadows */
