    --shadow-card: 0 4px 20px -4px hsl(223 47% 11% / 0.08);
    --shadow-card-hover: 0 8px 30px -8px hsl(223 47% 11% / 0.15);
    --shadow-glow: 0 0 40px hsl(239 84% 67% / 0.3);
    --shadow-service: 0 2px 12px -2px hsl(182 82% 31% / 0.12);
    --shadow-vault: 0 4px 16px -4px hsl(239 84% 67% / 0.15);

    /* Enhanced Transitions */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    --transition-fast: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);

    /* Error states */
