    /* Borders - light gray #E3E6EA */
    --border: 220 13% 91%;
    --input: 220 13% 91%;
    --ring: 182 82% 31%;

    /* 6px rounded corners */
    --radius: 0.375rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    /* Enhanced dark mode with better contrast ratios */
    --background: 220 15% 8%;
    --foreground: 0 0% 95%;

    /* Improved card colors for better contrast */
    --card: 220 15% 12%;
    --card-foreground: 0 0% 95%;

    --popover: 220 15% 12%;
    --popover-foreground: 0 0% 95%;

    /* Keep teal primary in dark mode */
    --primary: 182 82% 35%;
    --primary-foreground: 0 0% 100%;

    /* Better secondary colors */
    --secondary: 220 15% 18%;
    --secondary-foreground: 0 0% 90%;

    /* Improved muted colors for better readability */
    --muted: 220 15% 16%;
    --muted-foreground: 0 0% 75%;

    /* Brighter accent for better visibility */
    --accent: 239 84% 72%;
    --accent-foreground: 220 15% 8%;

    /* Enhanced SubCircle brand colors for dark mode */
