import { useState } from 'react';
import { formatDistanceToNow } from 'date-fns';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Input } from '@/components/ui/input';
import { useNotifications } from '@/hooks/use-notifications';
import { 
  CheckCheck, 
  Users, 
  UserX, 
  UserCheck, 
  Shield, 
  CreditCard, 
  AlertTriangle, 
  Bell,
  Trash2,
  Search,
  Filter,
  X,
  Key,
  Calendar,
  TrendingUp,
  Settings
} from 'lucide-react';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

const getNotificationIcon = (type: string, priority?: string) => {
  const iconClass = `h-4 w-4 ${
    priority === 'urgent' ? 'text-subcircle-error animate-pulse' :
    priority === 'high' ? 'text-subcircle-error' :
    priority === 'medium' ? 'text-subcircle-indigo' : 
    'text-muted-foreground'
  }`;

  switch (type) {
    case 'partner_accepted':
      return <UserCheck className={iconClass.replace('text-muted-foreground', 'text-emerald-500')} />;
    case 'partner_connected':
      return <Users className={iconClass.replace('text-muted-foreground', 'text-primary')} />;
    case 'partner_rejected':
      return <UserX className={iconClass.replace('text-muted-foreground', 'text-destructive')} />;
    case 'partner_request':
      return <Users className={iconClass.replace('text-muted-foreground', 'text-subcircle-indigo')} />;
    case 'credential_shared':
      return <Key className={iconClass.replace('text-muted-foreground', 'text-subcircle-cyan')} />;
    case 'credential_accessed':
      return <Shield className={iconClass.replace('text-muted-foreground', 'text-subcircle-teal')} />;
    case 'subscription_renewal':
      return <Calendar className={iconClass.replace('text-muted-foreground', 'text-amber-500')} />;
    case 'subscription_cost_change':
      return <TrendingUp className={iconClass.replace('text-muted-foreground', 'text-orange-500')} />;
    case 'security':
      return <Shield className={iconClass.replace('text-muted-foreground', 'text-subcircle-error')} />;
    case 'system':
      return <Settings className={iconClass.replace('text-muted-foreground', 'text-subcircle-indigo')} />;
    default:
      return <Bell className={iconClass} />;
  }
};

const getPriorityBadge = (priority?: string) => {
  switch (priority) {
    case 'urgent':
      return <Badge className="bg-subcircle-error text-white animate-pulse">Urgent</Badge>;
    case 'high':
      return <Badge className="bg-subcircle-error text-white">High</Badge>;
    case 'medium':
      return <Badge className="bg-subcircle-indigo text-white">Medium</Badge>;
    case 'low':
    default:
      return null;
  }
};

const getCategoryColor = (category?: string) => {
  switch (category) {
    case 'partner':
      return 'border-l-primary';
    case 'subscription':
      return 'border-l-subcircle-indigo';
    case 'security':
      return 'border-l-subcircle-error';
    case 'system':
      return 'border-l-subcircle-cyan';
    default:
      return 'border-l-muted';
  }
};

export const NotificationCenter = () => {
  const { 
    notifications, 
    allNotifications,
    loading, 
    unreadCount, 
    filter,
    setFilter,
    markAsRead, 
    markAllAsRead,
    deleteNotification,
    clearAllNotifications
  } = useNotifications();

  const [searchQuery, setSearchQuery] = useState('');

  const filteredNotifications = notifications.filter(notification =>
    notification.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    notification.message.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const categoryTabs = [
    { id: 'all', label: 'All', count: allNotifications.length },
    { id: 'partner', label: 'Partners', count: allNotifications.filter(n => n.category === 'partner').length },
    { id: 'subscription', label: 'Subscriptions', count: allNotifications.filter(n => n.category === 'subscription').length },
    { id: 'security', label: 'Security', count: allNotifications.filter(n => n.category === 'security').length },
    { id: 'system', label: 'System', count: allNotifications.filter(n => n.category === 'system').length },
  ];

  if (loading) {
    return (
      <div className="w-full max-w-2xl mx-auto">
        <div className="animate-pulse space-y-4 p-6">
          <div className="h-4 bg-muted rounded w-1/4"></div>
          <div className="space-y-3">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-2xl mx-auto bg-card rounded-lg border shadow-card animate-fade-in">
      {/* Header */}
      <div className="p-6 border-b bg-gradient-subtle">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <Bell className="h-6 w-6 text-primary" />
            <h2 className="text-xl font-semibold">Notification Center</h2>
            {unreadCount > 0 && (
              <Badge className="bg-subcircle-error text-white">
                {unreadCount}
              </Badge>
            )}
          </div>

          <div className="flex items-center gap-2">
            {unreadCount > 0 && (
              <Button
                variant="outline"
                size="sm"
                onClick={markAllAsRead}
                className="text-xs"
              >
                <CheckCheck className="h-3 w-3 mr-1" />
                Mark all read
              </Button>
            )}

            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline" size="sm">
                  <Settings className="h-3 w-3" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={clearAllNotifications}>
                  <Trash2 className="h-4 w-4 mr-2" />
                  Clear all notifications
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>

        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search notifications..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 pr-4"
          />
          {searchQuery && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setSearchQuery('')}
              className="absolute right-1 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0"
            >
              <X className="h-3 w-3" />
            </Button>
          )}
        </div>

        {/* Category Tabs */}
        <div className="flex gap-2 mt-4 overflow-x-auto">
          {categoryTabs.map((tab) => (
            <Button
              key={tab.id}
              variant={filter === tab.id ? "default" : "outline"}
              size="sm"
              onClick={() => setFilter(tab.id)}
              className="whitespace-nowrap"
            >
              {tab.label}
              {tab.count > 0 && (
                <Badge variant="secondary" className="ml-1 text-xs">
                  {tab.count}
                </Badge>
              )}
            </Button>
          ))}
        </div>
      </div>

      {/* Notification List */}
      <ScrollArea className="h-[500px]">
        {filteredNotifications.length === 0 ? (
          <div className="p-8 text-center text-muted-foreground">
            <Bell className="h-12 w-12 mx-auto mb-4 opacity-50" />
            <p className="text-lg mb-2">
              {searchQuery ? 'No matching notifications' : 'All caught up!'}
            </p>
            <p className="text-sm">
              {searchQuery ? 'Try adjusting your search terms.' : "You don't have any notifications right now."}
            </p>
          </div>
        ) : (
          <div className="divide-y">
            {filteredNotifications.map((notification) => (
              <div
                key={notification.id}
                className={`group relative p-4 hover:bg-accent/30 transition-all cursor-pointer border-l-4 ${getCategoryColor(notification.category)} ${
                  !notification.read ? 'bg-accent/10' : ''
                }`}
                onClick={() => !notification.read && markAsRead(notification.id)}
              >
                {/* Priority indicator */}
                {notification.priority === 'urgent' && (
                  <div className="absolute top-2 right-2">
                    <div className="w-2 h-2 bg-subcircle-error rounded-full animate-pulse"></div>
                  </div>
                )}

                <div className="flex items-start gap-3">
                  <div className="mt-1">
                    {getNotificationIcon(notification.type, notification.priority)}
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between gap-2 mb-2">
                      <div className="flex items-center gap-2 flex-wrap">
                        <h4 className="font-medium text-sm">
                          {notification.title}
                        </h4>
                        {!notification.read && (
                          <Badge variant="secondary" className="h-5 px-1.5 text-xs">
                            New
                          </Badge>
                        )}
                        {getPriorityBadge(notification.priority)}
                      </div>

                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteNotification(notification.id);
                        }}
                        className="opacity-0 group-hover:opacity-100 transition-opacity h-6 w-6 p-0"
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </div>

                    <p className="text-sm text-muted-foreground mb-3 leading-relaxed">
                      {notification.message}
                    </p>

                    <div className="flex items-center justify-between">
                      <p className="text-xs text-muted-foreground">
                        {formatDistanceToNow(new Date(notification.created_at), { addSuffix: true })}
                      </p>

                      {notification.action_url && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            // Handle action URL navigation
                            window.location.href = notification.action_url!;
                          }}
                          className="text-xs h-7"
                        >
                          View
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </ScrollArea>
    </div>
  );
};

src/hooks/use-push-notifications.ts.content:
import { useState, useEffect } from 'react';
import { useToast } from '@/hooks/use-toast';

export interface PushSubscription {
  endpoint: string;
  keys: {
    auth: string;
    p256dh: string;
  };
}

export const usePushNotifications = () => {
  const [isSupported, setIsSupported] = useState(false);
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [registration, setRegistration] = useState<ServiceWorkerRegistration | null>(null);
  const [subscription, setSubscription] = useState<PushSubscription | null>(null);
  const [vapidPublicKey, setVapidPublicKey] = useState<string | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    checkSupport();
    registerServiceWorker();
    fetchVapidPublicKey();
  }, []);

  const checkSupport = () => {
    const supported = 'serviceWorker' in navigator && 'PushManager' in window && 'Notification' in window;
    setIsSupported(supported);
    return supported;
  };

  const fetchVapidPublicKey = async () => {
    try {
      // TODO: Replace with actual backend API call
      // For now, set a mock VAPID key to allow testing
      const mockVapidKey = 'BCrMJj9_y5I0xqUWU3hN-OXX8cGQfU5bWDxpqKpbOOq-NJFT5IWZHKvKBXq5KxvKYXB_kKxgPQYBaQy1I0xqUWU';
      setVapidPublicKey(mockVapidKey);
      console.log('Mock VAPID public key set');
    } catch (error) {
      console.error('Error setting mock VAPID public key:', error);
    }
  };

  const registerServiceWorker = async () => {
    if (!checkSupport()) return;

    try {
      const reg = await navigator.serviceWorker.register('/sw.js');
      setRegistration(reg);

      // Check if already subscribed
      const existingSubscription = await reg.pushManager.getSubscription();
      if (existingSubscription) {
        setIsSubscribed(true);
        const subscriptionData: PushSubscription = {
          endpoint: existingSubscription.endpoint,
          keys: {
            auth: btoa(String.fromCharCode(...new Uint8Array(existingSubscription.getKey('auth')!))),
            p256dh: btoa(String.fromCharCode(...new Uint8Array(existingSubscription.getKey('p256dh')!)))
          }
        };
        setSubscription(subscriptionData);
      }
    } catch (error) {
      console.error('Service worker registration failed:', error);
      toast({
        title: "Service Worker Error",
        description: "Failed to register service worker for push notifications.",
        variant: "destructive"
      });
    }
  };

  const requestPermission = async (): Promise<NotificationPermission> => {
    if (!isSupported) {
      throw new Error('Push notifications are not supported');
    }

    // Check current permission status first
    const currentPermission = Notification.permission;
    console.log('Current notification permission:', currentPermission);

    if (currentPermission === 'granted') {
      return 'granted';
    }

    if (currentPermission === 'denied') {
      throw new Error('Notifications are blocked. Please enable them in your browser settings.');
    }

    // Request permission
    const permission = await Notification.requestPermission();
    console.log('Permission request result:', permission);
    return permission;
  };

  const subscribeToPushNotifications = async (): Promise<PushSubscription | null> => {
    if (isLoading) return null;

    setIsLoading(true);

    try {
      // Check if service worker is ready
      if (!registration) {
        throw new Error('Service worker not registered. Please refresh the page and try again.');
      }

      if (!vapidPublicKey) {
        throw new Error('VAPID public key not available. Please try again later.');
      }

      console.log('Requesting notification permission...');
      const permission = await requestPermission();

      if (permission !== 'granted') {
        const message = permission === 'denied' 
          ? "Notifications are blocked. Please enable them in your browser settings and refresh the page."
          : "Push notifications require permission to work. Please allow notifications when prompted.";

        toast({
          title: "Permission Required",
          description: message,
          variant: "destructive"
        });
        return null;
      }

      console.log('Subscribing to push manager...');

      // Convert VAPID key for browser compatibility
      const urlBase64ToUint8Array = (base64String: string) => {
        const padding = '='.repeat((4 - base64String.length % 4) % 4);
        const base64 = (base64String + padding)
          .replace(/-/g, '+')
          .replace(/_/g, '/');

        const rawData = window.atob(base64);
        const outputArray = new Uint8Array(rawData.length);

        for (let i = 0; i < rawData.length; ++i) {
          outputArray[i] = rawData.charCodeAt(i);
        }
        return outputArray;
      };

      const pushSubscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: urlBase64ToUint8Array(vapidPublicKey)
      });

      const subscriptionData: PushSubscription = {
        endpoint: pushSubscription.endpoint,
        keys: {
          auth: btoa(String.fromCharCode(...new Uint8Array(pushSubscription.getKey('auth')!))),
          p256dh: btoa(String.fromCharCode(...new Uint8Array(pushSubscription.getKey('p256dh')!)))
        }
      };

      // TODO: Store subscription in backend
      // For now, just log the subscription data
      console.log('Mock subscription storage:', subscriptionData);

      setIsSubscribed(true);
      setSubscription(subscriptionData);

      toast({
        title: "Push Notifications Enabled! ðŸ””",
        description: "You'll now receive push notifications for important updates.",
      });

      return subscriptionData;
    } catch (error: any) {
      console.error('Failed to subscribe to push notifications:', error);
      toast({
        title: "Subscription Failed",
        description: error.message || "Failed to enable push notifications. Please try again.",
        variant: "destructive"
      });
      return null;
    } finally {
      setIsLoading(false);
    }
  };

  const unsubscribeFromPushNotifications = async (): Promise<boolean> => {
    if (isLoading) return false;

    setIsLoading(true);

    try {
      if (!registration) {
        throw new Error('Service worker not registered');
      }

      const pushSubscription = await registration.pushManager.getSubscription();
      if (pushSubscription) {
        // TODO: Remove from backend
        // For now, just log the unsubscription
        if (subscription?.endpoint) {
          console.log('Mock subscription removal:', subscription.endpoint);
        }

        // Unsubscribe from browser
        await pushSubscription.unsubscribe();
      }

      setIsSubscribed(false);
      setSubscription(null);

      toast({
        title: "Push Notifications Disabled",
        description: "You will no longer receive push notifications.",
      });

      return true;
    } catch (error: any) {
      console.error('Failed to unsubscribe from push notifications:', error);
      toast({
        title: "Unsubscription Failed",
        description: error.message || "Failed to disable push notifications. Please try again.",
        variant: "destructive"
      });
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  const sendTestPushNotification = async () => {
    if (!isSubscribed || !subscription) {
      toast({
        title: "Not Subscribed",
        description: "Please enable push notifications first.",
        variant: "destructive"
      });
      return;
    }

    if (isLoading) return;
    setIsLoading(true);

    try {
      // TODO: Implement actual test notification sending
      // For now, just show a mock success message

      toast({
        title: "Test Notification Sent! ðŸš€",
        description: "Mock test notification sent. Check your notifications!",
      });
    } catch (error: any) {
      console.error('Failed to send test notification:', error);
      toast({
        title: "Test Failed",
        description: "Failed to send test notification (mock).",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  return {
    isSupported,
    isSubscribed,
    isLoading,
    subscription,
    registration,
    vapidPublicKey,
    requestPermission,
    subscribeToPushNotifications,
    unsubscribeFromPushNotifications,
    sendTestPushNotification
  };
};

src/components/ui/toggle.tsx.content:
import * as React from "react"
import * as TogglePrimitive from "@radix-ui/react-toggle"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const toggleVariants = cva(
  "inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors hover:bg-muted hover:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground",
  {
    variants: {
      variant: {
        default: "bg-transparent",
        outline:
          "border border-input bg-transparent hover:bg-accent hover:text-accent-foreground",
      },
      size: {
        default: "h-10 px-3",
        sm: "h-9 px-2.5",
        lg: "h-11 px-5",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

const Toggle = React.forwardRef<
  React.ElementRef<typeof TogglePrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof TogglePrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, ...props }, ref) => (
  <TogglePrimitive.Root
    ref={ref}
    className={cn(toggleVariants({ variant, size, className }))}
    {...props}
  />
))

Toggle.displayName = TogglePrimitive.Root.displayName

export { Toggle, toggleVariants }

src/pages/mobile/Link.tsx.content:
import { useState, useEffect } from "react";
import { MobileLayout } from "@/components/mobile/MobileLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Copy, Users, UserPlus, CheckCircle, Clock, X, Check } from "lucide-react";
// Removed Supabase import - using backend API instead
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/hooks/use-toast";
import { useNavigate } from "react-router-dom";

interface PartnerConnection {
  id: string;
  user_id: string;
  partner_id: string;
  status: string;
  created_at: string;
  isIncoming?: boolean;
  profiles?: {
    display_name?: string;
    avatar_url?: string;
  } | null;
}

// Demo data for showcasing link features
const demoConnections: PartnerConnection[] = [
  {
    id: 'demo-connection-1',
    user_id: 'demo-user',
    partner_id: 'demo-partner-sarah',
    status: 'active',
    created_at: '2024-01-15T10:00:00Z',
    isIncoming: false,
    profiles: {
      display_name: 'Sarah Johnson',
      avatar_url: null
    }
  },
  {
    id: 'demo-connection-2',
    user_id: 'demo-user',
    partner_id: 'demo-partner-alex',
    status: 'pending',
    created_at: '2024-01-20T14:30:00Z',
    isIncoming: true,
    profiles: {
      display_name: 'Alex Smith',
      avatar_url: null
    }
  }
];

const demoCodes = [
  { code: 'DEMO-SAR', name: 'Sarah Johnson' },
  { code: 'DEMO-ALE', name: 'Alex Smith' },
  { code: 'DEMO-MIK', name: 'Mike Wilson' }
];

export default function Link() {
  const [myLinkCode, setMyLinkCode] = useState("");
  const [partnerCode, setPartnerCode] = useState("");
  const [connections, setConnections] = useState<PartnerConnection[]>([]);
  const [loading, setLoading] = useState(true);
  const { user, isDemoMode } = useAuth();
  const { toast } = useToast();
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      generateMyLinkCode();
      fetchConnections();
    } else {
      setLoading(false);
    }
  }, [user]);

  const generateMyLinkCode = () => {
    if (!user) return;

    if (isDemoMode) {
      // Use a different code for demo mode to avoid self-connection issues
      setMyLinkCode('DEMO-YOU');
    } else {
      // Generate a simple link code based on user ID (first 8 characters)
      const code = user.id.substring(0, 8).toUpperCase();
      setMyLinkCode(code);
    }
  };

  const fetchConnections = async () => {
    if (!user) return;

    try {
      if (isDemoMode) {
        // Use demo data to showcase features
        setTimeout(() => {
          setConnections(demoConnections);
          setLoading(false);
        }, 800); // Simulate loading time
      } else {
        // TODO: Replace with actual backend API call
        // For now, set empty connections to allow the app to build
        setConnections([]);
        setLoading(false);
      }
    } catch (error) {
      console.error("Error fetching connections:", error);
      setLoading(false);
    }
  };

  const copyLinkCode = async () => {
    try {
      await navigator.clipboard.writeText(myLinkCode);
      toast({
        title: "Copied!",
        description: "Your link code has been copied to clipboard",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy link code",
        variant: "destructive",
      });
    }
  };

  const sendPartnerRequest = async () => {
    if (!user || !partnerCode.trim()) return;

    try {
      setLoading(true);

      // Check for self-connection
      const myCode = isDemoMode ? 'DEMO-YOU' : user.id.substring(0, 8).toUpperCase();
      if (partnerCode === myCode) {
        toast({
          title: "Invalid code",
          description: "You cannot connect to yourself",
          variant: "destructive",
        });
        return;
      }

      // In demo mode, check if it's a valid demo code
      if (isDemoMode) {
        const validDemoCode = demoCodes.find(demo => demo.code === partnerCode);
        if (!validDemoCode) {
          toast({
            title: "Invalid demo code",
            description: "Try DEMO-SAR, DEMO-ALE, or DEMO-MIK",
            variant: "destructive",
          });
          return;
        }

        // Check if already connected
        const alreadyConnected = connections.some(
          conn => conn.profiles?.display_name === validDemoCode.name
        );
        if (alreadyConnected) {
          toast({
            title: "Already connected",
            description: `You are already connected to ${validDemoCode.name}`,
            variant: "destructive",
          });
          return;
        }

        toast({
          title: "Demo connection created!",
          description: `Connected to ${validDemoCode.name} (demo mode)`,
        });
      } else {
        // Simulate sending partner request for non-demo mode
        toast({
          title: "Request sent!",
          description: `Connection request sent (simulated)`,
        });
      }

      setPartnerCode("");
      await fetchConnections(); // Refresh the list

    } catch (error) {
      console.error("Error sending partner request:", error);
      toast({
        title: "Error",
        description: "Failed to send connection request",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active':
        return <CheckCircle className="w-4 h-4 text-success-green" />;
      case 'pending':
        return <Clock className="w-4 h-4 text-yellow-500" />;
      case 'declined':
        return <X className="w-4 h-4 text-destructive" />;
      default:
        return <Clock className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'bg-success-green/10 text-success-green border-success-green/20';
      case 'pending':
        return 'bg-yellow-500/10 text-yellow-600 border-yellow-500/20';
      case 'declined':
        return 'bg-destructive/10 text-destructive border-destructive/20';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  const handleConnectionAction = async (connectionId: string, action: 'accept' | 'decline') => {
    try {
      // TODO: Implement backend API call
      // For now, just simulate success

      toast({
        title: action === 'accept' ? "Connection accepted!" : "Connection declined",
        description: action === 'accept' 
          ? "You can now compare subscriptions with this partner" 
          : "Connection request has been declined",
      });

      await fetchConnections(); // Refresh the list

    } catch (error) {
      console.error("Error updating connection:", error);
      toast({
        title: "Error",
        description: "Failed to update connection",
        variant: "destructive",
      });
    }
  };

  if (!user) {
    return (
      <MobileLayout>
        <div className="p-mobile-padding">
          <div className="text-center py-12 space-y-4">
            <Users className="w-16 h-16 mx-auto text-muted-foreground" />
            <div className="space-y-2">
              <h2 className="text-xl font-semibold">Partner Connections</h2>
              <p className="text-muted-foreground">
                Please sign in to connect with family and friends
              </p>
            </div>
            <Button onClick={() => navigate("/onboarding")} className="bg-primary text-primary-foreground">
              Sign In
            </Button>
          </div>
        </div>
      </MobileLayout>
    );
  }

  if (loading) {
    return (
      <MobileLayout>
        <div className="p-mobile-padding">
          <div className="flex items-center justify-center h-64">
            <div className="animate-pulse text-muted-foreground">Loading connections...</div>
          </div>
        </div>
      </MobileLayout>
    );
  }

  return (
    <MobileLayout>
      <div className="p-mobile-padding space-y-6">
        {/* Header */}
        <div className="space-y-2">
          <h1 className="text-2xl font-bold text-foreground">Link Partners</h1>
          <p className="text-muted-foreground text-sm">
            Connect with family and friends to share subscriptions
          </p>
        </div>

        {/* My Link Code */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-lg flex items-center gap-2">
              <Users className="w-5 h-5" />
              Your Link Code
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <p className="text-sm text-muted-foreground">
                Share this code with family or friends to connect
              </p>
              <div className="flex gap-2">
                <div className="flex-1 p-3 bg-muted rounded-lg text-center">
                  <span className="text-2xl font-bold font-mono tracking-wider">
                    {myLinkCode}
                  </span>
                </div>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={copyLinkCode}
                  className="shrink-0"
                >
                  <Copy className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Enter Partner Code */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-lg flex items-center gap-2">
              <UserPlus className="w-5 h-5" />
              Connect with Partner
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <p className="text-sm text-muted-foreground">
                Enter your partner's link code to connect
              </p>
              <div className="flex gap-2">
                <Input
                  placeholder="Enter partner code"
                  value={partnerCode}
                  onChange={(e) => setPartnerCode(e.target.value.toUpperCase())}
                  className="flex-1 text-center font-mono tracking-wider"
                  maxLength={8}
                />
                <Button
                  onClick={sendPartnerRequest}
                  disabled={!partnerCode.trim()}
                  className="shrink-0 bg-accent text-accent-foreground"
                >
                  Connect
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Active Connections */}
        <div className="space-y-4">
          <h2 className="text-lg font-semibold flex items-center gap-2">
            <CheckCircle className="w-5 h-5 text-success-green" />
            Your Connections
            {connections.length > 0 && (
              <Badge variant="secondary" className="ml-auto">
                {connections.length}
              </Badge>
            )}
          </h2>

          {connections.length > 0 ? (
            <div className="space-y-3">
              {connections.map((connection) => (
                <Card key={connection.id}>
                  <CardContent className="p-4">
                     <div className="flex items-center justify-between">
                       <div className="flex items-center gap-3">
                         <div className="w-10 h-10 bg-gradient-primary rounded-full flex items-center justify-center text-white font-semibold">
                           {connection.profiles?.display_name?.charAt(0) || "?"}
                         </div>
                         <div>
                           <h3 className="font-medium">
                             {connection.profiles?.display_name || "Unknown User"}
                           </h3>
                           <p className="text-xs text-muted-foreground">
                             {connection.status === 'pending' && connection.isIncoming 
                               ? "Wants to connect with you"
                               : `Connected ${new Date(connection.created_at).toLocaleDateString()}`
                             }
                           </p>
                         </div>
                       </div>

                       <div className="flex items-center gap-2">
                         {connection.status === 'pending' && connection.isIncoming ? (
                           <div className="flex gap-1">
                             <Button
                               size="sm"
                               variant="outline"
                               onClick={() => handleConnectionAction(connection.id, 'accept')}
                               className="h-8 px-3"
                             >
                               <Check className="w-3 h-3 mr-1" />
                               Accept
                             </Button>
                             <Button
                               size="sm"
                               variant="outline"
                               onClick={() => handleConnectionAction(connection.id, 'decline')}
                               className="h-8 px-3 text-destructive border-destructive/20 hover:bg-destructive/10"
                             >
                               <X className="w-3 h-3 mr-1" />
                               Decline
                             </Button>
                           </div>
                         ) : (
                           <div className="flex items-center gap-2">
                             {getStatusIcon(connection.status)}
                             <Badge
                               variant="outline"
                               className={getStatusColor(connection.status)}
                             >
                               {connection.status.charAt(0).toUpperCase() + connection.status.slice(1)}
                             </Badge>
                           </div>
                         )}
                       </div>
                     </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <Card>
              <CardContent className="p-6 text-center space-y-3">
                <Users className="w-12 h-12 mx-auto text-muted-foreground" />
                <div className="space-y-1">
                  <h3 className="font-medium">No connections yet</h3>
                  <p className="text-sm text-muted-foreground">
                    Share your link code or enter a partner's code to get started
                  </p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Help section */}
        <Card className="border-subcircle-cyan/20 bg-subcircle-cyan/5">
          <CardContent className="p-4">
